function DonorSearchScreen({ onBack }) {
  const [bloodType, setBloodType] = useState('O-');
  const [component, setComponent] = useState('All');
  const [results, setResults] = useState([]);

  const search = () => {
    const filtered = mockInventory.filter(
      item => item.bloodType === bloodType && (component === 'All' || item.component === component)
    );
    setResults(filtered);
  };

  useEffect(() => {
    search();
  }, []);

  return (
    <ScrollView style={styles.container}>
      <View style={styles.safeContainer}>
        <TouchableOpacity onPress={onBack}>
          <Text style={styles.link}>‚Üê Back to roles</Text>
        </TouchableOpacity>

        <Text style={styles.header}>Search Blood & Platelets</Text>

        <Text style={styles.label}>Blood Type</Text>
        <TextInput
          style={styles.input}
          placeholder="e.g., O-, A+, B-"
          value={bloodType}
          onChangeText={setBloodType}
        />

        <Text style={styles.label}>Component</Text>
        <TextInput
          style={styles.input}
          placeholder="e.g., Whole Blood, Platelets, Plasma"
          value={component}
          onChangeText={setComponent}
        />

        <TouchableOpacity style={styles.button} onPress={search}>
          <Text style={styles.buttonText}>Search</Text>
        </TouchableOpacity>

        <Text style={styles.subheader}>Results ({results.length})</Text>
        {results.length === 0 ? (
          <Text style={{ color: '#6b7280', fontSize: 14 }}>No blood inventory found. Try a different search.</Text>
        ) : (
          <FlatList
            scrollEnabled={false}
            data={results}
            keyExtractor={item => item.id.toString()}
            renderItem={({ item }) => (
              <View style={styles.card}>
                <Text style={styles.cardTitle}>{item.institution}</Text>
                <Text style={styles.cardText}>
                  {item.bloodType} ‚Ä¢ {item.component} ‚Ä¢ {item.units} units
                </Text>
                <TouchableOpacity style={styles.button} onPress={() => Alert.alert('Contact', `Call ${item.phone}`)}>
                  <Text style={styles.buttonText}>üìû Call Institution</Text>
                </TouchableOpacity>
              </View>
            )}
          />
        )}

        <Text style={styles.subheader}>Nearby Blood Drives</Text>
        <FlatList
          scrollEnabled={false}
          data={mockDrives}
          keyExtractor={item => item.id.toString()}
          renderItem={({ item }) => (
            <View style={styles.card}>
              <Text style={styles.cardTitle}>{item.name}</Text>
              <Text style={styles.cardText}>üìÖ {item.date}</Text>
              <Text style={styles.cardText}>üìç {item.location}</Text>
              <Text style={styles.cardText}>By: {item.organizer}</Text>
            </View>
          )}
        />
      </View>
    </ScrollView>
  );
}

function HospitalDashboard({ onBack }) {
  const [activeTab, setActiveTab] = useState('inventory');
  const [inventory, setInventory] = useState([
    { id: 1, bloodType: 'O+', component: 'Whole Blood', units: 25 },
    { id: 2, bloodType: 'O-', component: 'Platelets', units: 10 },
  ]);
  const [newBloodType, setNewBloodType] = useState('');
  const [newComponent, setNewComponent] = useState('');
  const [newUnits, setNewUnits] = useState('');

  const addInventory = () => {
    if (!newBloodType || !newComponent || !newUnits) {
      Alert.alert('Error', 'Please fill all fields');
      return;
    }
    setInventory([...inventory, { id: Date.now(), bloodType: newBloodType, component: newComponent, units: parseInt(newUnits) }]);
    setNewBloodType('');
    setNewComponent('');
    setNewUnits('');
    Alert.alert('Success', 'Inventory item added');
  };

  const removeInventory = (id) => {
    setInventory(inventory.filter(i => i.id !== id));
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.safeContainer}>
        <TouchableOpacity onPress={onBack}>
          <Text style={styles.link}>‚Üê Back to roles</Text>
        </TouchableOpacity>

        <Text style={styles.header}>üè• Hospital Dashboard</Text>
        <View style={{ flexDirection: 'row', justifyContent: 'space-around', marginBottom: 16 }}>
          <TouchableOpacity onPress={() => setActiveTab('inventory')} style={{ padding: 8, borderBottomWidth: activeTab === 'inventory' ? 2 : 0, borderBottomColor: '#ef4444' }}>
            <Text style={{ fontWeight: '600' }}>Inventory</Text>
          </TouchableOpacity>
          <TouchableOpacity onPress={() => setActiveTab('requests')} style={{ padding: 8, borderBottomWidth: activeTab === 'requests' ? 2 : 0, borderBottomColor: '#ef4444' }}>
            <Text style={{ fontWeight: '600' }}>Requests</Text>
          </TouchableOpacity>
        </View>

        {activeTab === 'inventory' && (
          <>
            <Text style={styles.subheader}>Manage Inventory</Text>
            <Text style={styles.label}>Blood Type</Text>
            <TextInput style={styles.input} placeholder="O+, A-, etc." value={newBloodType} onChangeText={setNewBloodType} />
            <Text style={styles.label}>Component</Text>
            <TextInput style={styles.input} placeholder="Whole Blood, Platelets, Plasma" value={newComponent} onChangeText={setNewComponent} />
            <Text style={styles.label}>Units</Text>
            <TextInput style={styles.input} placeholder="10" value={newUnits} onChangeText={setNewUnits} keyboardType="number-pad" />
            <TouchableOpacity style={styles.button} onPress={addInventory}>
              <Text style={styles.buttonText}>+ Add Item</Text>
            </TouchableOpacity>

            <Text style={styles.subheader}>Current Stock</Text>
            <FlatList
              scrollEnabled={false}
              data={inventory}
              keyExtractor={item => item.id.toString()}
              renderItem={({ item }) => (
                <View style={styles.card}>
                  <Text style={styles.cardTitle}>{item.bloodType} ‚Ä¢ {item.component}</Text>
                  <Text style={styles.cardText}>{item.units} units available</Text>
                  <TouchableOpacity onPress={() => removeInventory(item.id)}>
                    <Text style={{ color: '#dc2626', fontSize: 12, marginTop: 8 }}>Remove</Text>
                  </TouchableOpacity>
                </View>
              )}
            />
          </>
        )}

        {activeTab === 'requests' && (
          <>
            <Text style={styles.subheader}>Post Urgent Request</Text>
            <TouchableOpacity style={styles.button} onPress={() => Alert.alert('Success', 'Urgent request posted!\n\nMatching donors will be notified.')}>
              <Text style={styles.buttonText}>üì¢ Post New Request</Text>
            </TouchableOpacity>

            <Text style={styles.subheader}>Open Requests</Text>
            <FlatList
              scrollEnabled={false}
              data={mockRequests.filter(r => r.institution === 'City General Hospital')}
              keyExtractor={item => item.id.toString()}
              renderItem={({ item }) => (
                <View style={styles.card}>
                  <Text style={styles.cardTitle}>{item.bloodType} {item.component}</Text>
                  <Text style={styles.cardText}>Needed: {item.units} units</Text>
                  <View style={styles.badge}>
                    <Text style={styles.badgeText}>{item.urgency.toUpperCase()}</Text>
                  </View>
                </View>
              )}
            />
          </>
        )}
      </View>
    </ScrollView>
  );
}

