import React, { useState } from 'react';
import { View, Text, TextInput, Button, FlatList, TouchableOpacity, StyleSheet, Alert, ScrollView } from 'react-native';

// ============= TYPE DEFINITIONS =============

interface User {
  phone?: string;
  verified?: boolean;
  fullName?: string;
  bloodType?: string;
}

interface InventoryItem {
  id: number;
  bloodType: string;
  component: string;
  units: number;
}

interface MockInventory extends InventoryItem {
  institution: string;
  phone: string;
}

interface BloodDrive {
  id: number;
  name: string;
  date: string;
  location: string;
}

interface BloodRequest {
  id: number;
  institution: string;
  bloodType: string;
  component: string;
  units: number;
  urgency: 'critical' | 'high' | 'medium';
}

// ============= STYLES =============

const styles = StyleSheet.create({
  appContainer: { flex: 1, backgroundColor: '#fff' },
  container: { flex: 1, padding: 16 },
  safeContainer: { maxWidth: 800, width: '100%', marginHorizontal: 'auto', flex: 1 },
  scrollContent: { flexGrow: 1 },
  header: { fontSize: 28, fontWeight: 'bold', color: '#e11d48', marginBottom: 8 },
  subheader: { fontSize: 18, fontWeight: '600', marginBottom: 12, color: '#1f2937' },
  label: { fontSize: 14, fontWeight: '600', color: '#374151', marginTop: 12, marginBottom: 6 },
  input: { borderWidth: 1, borderColor: '#d1d5db', padding: 12, marginBottom: 12, borderRadius: 6, fontSize: 14 },
  button: { backgroundColor: '#ef4444', padding: 14, borderRadius: 6, marginBottom: 12, justifyContent: 'center', alignItems: 'center' },
  buttonText: { color: '#fff', fontWeight: '600', fontSize: 14 },
  backButton: { paddingVertical: 8, marginBottom: 16 },
  backButtonText: { color: '#0066cc', fontSize: 14, fontWeight: '600' },
  card: { padding: 12, borderWidth: 1, borderColor: '#e5e7eb', borderRadius: 6, marginBottom: 12, backgroundColor: '#f9fafb' },
  cardTitle: { fontWeight: '700', fontSize: 16, marginBottom: 4, color: '#1f2937' },
  cardText: { fontSize: 14, color: '#6b7280', marginBottom: 4 },
  badge: { backgroundColor: '#fecaca', paddingHorizontal: 8, paddingVertical: 4, borderRadius: 4, alignSelf: 'flex-start', marginTop: 4 },
  badgeText: { fontSize: 12, color: '#991b1b', fontWeight: '600' },
  tabContainer: { flexDirection: 'row', marginBottom: 16, borderBottomWidth: 2, borderBottomColor: '#e5e7eb' },
  tab: { flex: 1, paddingVertical: 12, alignItems: 'center' },
  tabActive: { borderBottomWidth: 2, borderBottomColor: '#ef4444' },
  tabText: { fontWeight: '600', fontSize: 14 },
  tabTextActive: { color: '#ef4444' },
});

const mockInventory = [
  { id: 1, bloodType: 'O-', component: 'Whole Blood', units: 12, institution: 'City General Hospital', phone: '+1 555-0100' },
  { id: 2, bloodType: 'O-', component: 'Platelets', units: 8, institution: 'Red Cross Blood Bank', phone: '+1 555-0200' },
  { id: 3, bloodType: 'A+', component: 'Plasma', units: 5, institution: 'Central Blood Lab', phone: '+1 555-0300' },
  { id: 4, bloodType: 'B+', component: 'Whole Blood', units: 15, institution: 'Emergency Care Hospital', phone: '+1 555-0400' },
];

const mockDrives = [
  { id: 1, name: 'Summer Blood Drive 2025', date: '2025-12-15', location: 'Downtown Community Center' },
  { id: 2, name: 'Holiday Donation Event', date: '2025-12-20', location: 'Central Hospital' },
];

const mockRequests = [
  { id: 1, institution: 'City General Hospital', bloodType: 'O-', component: 'Whole Blood', units: 5, urgency: 'critical' },
  { id: 2, institution: 'Emergency Care Hospital', bloodType: 'AB+', component: 'Plasma', units: 3, urgency: 'high' },
];

// ============= AUTH SCREENS =============

function LoginScreen({ onLogin }: { onLogin: (credentials: User) => void }) {
  const [phone, setPhone] = useState('+1 ');
  const [otp, setOtp] = useState('');
  const [otpSent, setOtpSent] = useState(false);

  const sendOTP = () => {
    if (!phone || phone.length < 5) {
      Alert.alert('Error', 'Please enter a valid phone number');
      return;
    }
    setOtpSent(true);
    Alert.alert('OTP Sent', `Verification code sent to ${phone}`);
  };

  const confirmOTP = () => {
    if (!otp || otp.length < 4) {
      Alert.alert('Error', 'Please enter a valid OTP');
      return;
    }
    onLogin({ phone, verified: true });
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.safeContainer}>
        <Text style={styles.header}>BloodConnect</Text>
        <Text style={{ fontSize: 16, color: '#6b7280', marginBottom: 24 }}>Sign in to continue</Text>

        {!otpSent ? (
          <>
            <Text style={styles.label}>Phone Number</Text>
            <TextInput
              style={styles.input}
              placeholder="+1 555 555 5555"
              value={phone}
              onChangeText={setPhone}
              keyboardType="phone-pad"
            />
            <TouchableOpacity style={styles.button} onPress={sendOTP}>
              <Text style={styles.buttonText}>Send OTP</Text>
            </TouchableOpacity>
          </>
        ) : (
          <>
            <Text style={styles.label}>Enter OTP</Text>
            <TextInput
              style={styles.input}
              placeholder="123456"
              value={otp}
              onChangeText={setOtp}
              keyboardType="number-pad"
            />
            <TouchableOpacity style={styles.button} onPress={confirmOTP}>
              <Text style={styles.buttonText}>Verify OTP</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => setOtpSent(false)}>
              <Text style={{ color: '#0066cc', textAlign: 'center', marginTop: 12 }}>Change phone number</Text>
            </TouchableOpacity>
          </>
        )}
      </View>
    </ScrollView>
  );
}

function ProfileScreen({ onProfileComplete }: { onProfileComplete: (profile: { fullName: string; bloodType: string }) => void }) {
  const [fullName, setFullName] = useState('');
  const [bloodType, setBloodType] = useState('O+');

  const handleComplete = () => {
    if (!fullName) {
      Alert.alert('Error', 'Please enter your name');
      return;
    }
    onProfileComplete({ fullName, bloodType });
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.safeContainer}>
        <Text style={styles.header}>Complete Profile</Text>

        <Text style={styles.label}>Full Name</Text>
        <TextInput
          style={styles.input}
          placeholder="John Doe"
          value={fullName}
          onChangeText={setFullName}
        />

        <Text style={styles.label}>Blood Type</Text>
        <TextInput
          style={styles.input}
          placeholder="O+, A-, B+, etc."
          value={bloodType}
          onChangeText={setBloodType}
        />

        <TouchableOpacity style={styles.button} onPress={handleComplete}>
          <Text style={styles.buttonText}>Continue</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

// ============= ROLE SELECTION =============

function RoleSelectScreen({ onSelectRole }: { onSelectRole: (role: 'donor' | 'hospital' | 'bank' | 'lab') => void }) {
  const roles: Array<{ key: 'donor' | 'hospital' | 'bank' | 'lab'; label: string; desc: string }> = [
    { key: 'donor', label: 'ü©∏ Donor / Patient', desc: 'Find blood & platelets near you' },
    { key: 'hospital', label: 'üè• Hospital', desc: 'Manage inventory & post requests' },
    { key: 'bank', label: 'ü©π Blood Bank', desc: 'Manage stock & blood drives' },
    { key: 'lab', label: 'üî¨ Blood Lab', desc: 'Manage lab inventory' },
  ];

  return (
    <ScrollView style={styles.container}>
      <View style={styles.safeContainer}>
        <Text style={styles.header}>Select Your Role</Text>
        <Text style={{ fontSize: 14, color: '#6b7280', marginBottom: 24 }}>Choose how you want to use BloodConnect</Text>

        {roles.map(r => (
          <TouchableOpacity key={r.key} onPress={() => onSelectRole(r.key)} style={styles.card}>
            <Text style={styles.cardTitle}>{r.label}</Text>
            <Text style={styles.cardText}>{r.desc}</Text>
          </TouchableOpacity>
        ))}
      </View>
    </ScrollView>
  );
}

// ============= DONOR SCREEN =============

function DonorScreen({ onLogout }: { onLogout: () => void }) {
  const [searchBloodType, setSearchBloodType] = useState('O-');
  const [searchComponent, setSearchComponent] = useState('All');
  const [results, setResults] = useState(mockInventory);

  const search = () => {
    const filtered = mockInventory.filter(
      item => item.bloodType === searchBloodType && (searchComponent === 'All' || item.component === searchComponent)
    );
    setResults(filtered);
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.safeContainer}>
        <TouchableOpacity style={styles.backButton} onPress={onLogout}>
          <Text style={styles.backButtonText}>‚Üê Logout</Text>
        </TouchableOpacity>

        <Text style={styles.header}>Find Blood & Platelets</Text>

        <Text style={styles.label}>Blood Type</Text>
        <TextInput
          style={styles.input}
          placeholder="O-, A+, B-, etc."
          value={searchBloodType}
          onChangeText={setSearchBloodType}
        />

        <Text style={styles.label}>Component</Text>
        <TextInput
          style={styles.input}
          placeholder="Whole Blood, Platelets, Plasma"
          value={searchComponent}
          onChangeText={setSearchComponent}
        />

        <TouchableOpacity style={styles.button} onPress={search}>
          <Text style={styles.buttonText}>üîç Search</Text>
        </TouchableOpacity>

        <Text style={styles.subheader}>Search Results ({results.length})</Text>
        {results.length === 0 ? (
          <Text style={{ color: '#6b7280' }}>No inventory found</Text>
        ) : (
          <FlatList
            scrollEnabled={false}
            data={results}
            keyExtractor={item => item.id.toString()}
            renderItem={({ item }) => (
              <View style={styles.card}>
                <Text style={styles.cardTitle}>{item.institution}</Text>
                <Text style={styles.cardText}>{item.bloodType} ‚Ä¢ {item.component} ‚Ä¢ {item.units} units</Text>
                <TouchableOpacity style={styles.button} onPress={() => Alert.alert('Contact', `Call ${item.phone}`)}>
                  <Text style={styles.buttonText}>üìû Contact</Text>
                </TouchableOpacity>
              </View>
            )}
          />
        )}

        <Text style={styles.subheader}>Blood Drives</Text>
        <FlatList
          scrollEnabled={false}
          data={mockDrives}
          keyExtractor={item => item.id.toString()}
          renderItem={({ item }) => (
            <View style={styles.card}>
              <Text style={styles.cardTitle}>{item.name}</Text>
              <Text style={styles.cardText}>üìÖ {item.date} | üìç {item.location}</Text>
            </View>
          )}
        />
      </View>
    </ScrollView>
  );
}

// ============= HOSPITAL SCREEN =============

function HospitalScreen({ onLogout }: { onLogout: () => void }) {
  const [activeTab, setActiveTab] = useState('inventory');
  const [inventory, setInventory] = useState([
    { id: 1, bloodType: 'O+', component: 'Whole Blood', units: 25 },
    { id: 2, bloodType: 'O-', component: 'Platelets', units: 10 },
  ]);
  const [newBloodType, setNewBloodType] = useState('');
  const [newComponent, setNewComponent] = useState('');
  const [newUnits, setNewUnits] = useState('');

  const addInventory = () => {
    if (!newBloodType || !newComponent || !newUnits) {
      Alert.alert('Error', 'Fill all fields');
      return;
    }
    setInventory([...inventory, { id: Date.now(), bloodType: newBloodType, component: newComponent, units: parseInt(newUnits) || 0 }]);
    setNewBloodType('');
    setNewComponent('');
    setNewUnits('');
    Alert.alert('Success', 'Added to inventory');
  };

  const removeItem = (id: number) => {
    setInventory(inventory.filter(i => i.id !== id));
  };

  return (
    <ScrollView style={styles.container}>
      <View style={styles.safeContainer}>
        <TouchableOpacity style={styles.backButton} onPress={onLogout}>
          <Text style={styles.backButtonText}>‚Üê Logout</Text>
        </TouchableOpacity>

        <Text style={styles.header}>üè• Hospital Dashboard</Text>

        <View style={styles.tabContainer}>
          <TouchableOpacity style={[styles.tab, activeTab === 'inventory' && styles.tabActive]} onPress={() => setActiveTab('inventory')}>
            <Text style={[styles.tabText, activeTab === 'inventory' && styles.tabTextActive]}>Inventory</Text>
          </TouchableOpacity>
          <TouchableOpacity style={[styles.tab, activeTab === 'requests' && styles.tabActive]} onPress={() => setActiveTab('requests')}>
            <Text style={[styles.tabText, activeTab === 'requests' && styles.tabTextActive]}>Requests</Text>
          </TouchableOpacity>
        </View>

        {activeTab === 'inventory' && (
          <>
            <Text style={styles.subheader}>Add Inventory</Text>
            <Text style={styles.label}>Blood Type</Text>
            <TextInput style={styles.input} placeholder="O+" value={newBloodType} onChangeText={setNewBloodType} />
            <Text style={styles.label}>Component</Text>
            <TextInput style={styles.input} placeholder="Whole Blood" value={newComponent} onChangeText={setNewComponent} />
            <Text style={styles.label}>Units</Text>
            <TextInput style={styles.input} placeholder="10" value={newUnits} onChangeText={setNewUnits} keyboardType="number-pad" />
            <TouchableOpacity style={styles.button} onPress={addInventory}>
              <Text style={styles.buttonText}>+ Add Item</Text>
            </TouchableOpacity>

            <Text style={styles.subheader}>Current Stock</Text>
            <FlatList
              scrollEnabled={false}
              data={inventory}
              keyExtractor={item => item.id.toString()}
              renderItem={({ item }) => (
                <View style={styles.card}>
                  <Text style={styles.cardTitle}>{item.bloodType} ‚Ä¢ {item.component}</Text>
                  <Text style={styles.cardText}>{item.units} units</Text>
                  <TouchableOpacity onPress={() => removeItem(item.id)}>
                    <Text style={{ color: '#dc2626', fontSize: 12, marginTop: 8 }}>Remove</Text>
                  </TouchableOpacity>
                </View>
              )}
            />
          </>
        )}

        {activeTab === 'requests' && (
          <>
            <Text style={styles.subheader}>Post Urgent Request</Text>
            <TouchableOpacity style={styles.button} onPress={() => Alert.alert('Success', 'Urgent request posted!')}>
              <Text style={styles.buttonText}>üì¢ Post Request</Text>
            </TouchableOpacity>

            <Text style={styles.subheader}>Open Requests</Text>
            <FlatList
              scrollEnabled={false}
              data={mockRequests}
              keyExtractor={item => item.id.toString()}
              renderItem={({ item }) => (
                <View style={styles.card}>
                  <Text style={styles.cardTitle}>{item.bloodType} {item.component}</Text>
                  <Text style={styles.cardText}>Need: {item.units} units</Text>
                  <View style={styles.badge}>
                    <Text style={styles.badgeText}>{item.urgency.toUpperCase()}</Text>
                  </View>
                </View>
              )}
            />
          </>
        )}
      </View>
    </ScrollView>
  );
}

// ============= BLOOD BANK SCREEN =============

function BloodBankScreen({ onLogout }: { onLogout: () => void }) {
  const [activeTab, setActiveTab] = useState('inventory');

  return (
    <ScrollView style={styles.container}>
      <View style={styles.safeContainer}>
        <TouchableOpacity style={styles.backButton} onPress={onLogout}>
          <Text style={styles.backButtonText}>‚Üê Logout</Text>
        </TouchableOpacity>

        <Text style={styles.header}>ü©π Blood Bank Dashboard</Text>

        <View style={styles.tabContainer}>
          <TouchableOpacity style={[styles.tab, activeTab === 'inventory' && styles.tabActive]} onPress={() => setActiveTab('inventory')}>
            <Text style={[styles.tabText, activeTab === 'inventory' && styles.tabTextActive]}>Inventory</Text>
          </TouchableOpacity>
          <TouchableOpacity style={[styles.tab, activeTab === 'drives' && styles.tabActive]} onPress={() => setActiveTab('drives')}>
            <Text style={[styles.tabText, activeTab === 'drives' && styles.tabTextActive]}>Drives</Text>
          </TouchableOpacity>
        </View>

        {activeTab === 'inventory' && (
          <>
            <Text style={styles.subheader}>Current Stock</Text>
            <FlatList
              scrollEnabled={false}
              data={[
                { id: 1, bloodType: 'O+', component: 'Whole Blood', units: 40 },
                { id: 2, bloodType: 'O-', component: 'Platelets', units: 25 },
              ]}
              keyExtractor={item => item.id.toString()}
              renderItem={({ item }) => (
                <View style={styles.card}>
                  <Text style={styles.cardTitle}>{item.bloodType} ‚Ä¢ {item.component}</Text>
                  <Text style={styles.cardText}>{item.units} units</Text>
                </View>
              )}
            />
          </>
        )}

        {activeTab === 'drives' && (
          <>
            <Text style={styles.subheader}>Schedule Drive</Text>
            <TouchableOpacity style={styles.button} onPress={() => Alert.alert('Success', 'Blood drive scheduled!')}>
              <Text style={styles.buttonText}>+ Create Drive</Text>
            </TouchableOpacity>

            <Text style={styles.subheader}>Upcoming Drives</Text>
            <FlatList
              scrollEnabled={false}
              data={mockDrives}
              keyExtractor={item => item.id.toString()}
              renderItem={({ item }) => (
                <View style={styles.card}>
                  <Text style={styles.cardTitle}>{item.name}</Text>
                  <Text style={styles.cardText}>üìÖ {item.date} | üìç {item.location}</Text>
                </View>
              )}
            />
          </>
        )}
      </View>
    </ScrollView>
  );
}

// ============= BLOOD LAB SCREEN =============

function BloodLabScreen({ onLogout }: { onLogout: () => void }) {
  return (
    <ScrollView style={styles.container}>
      <View style={styles.safeContainer}>
        <TouchableOpacity style={styles.backButton} onPress={onLogout}>
          <Text style={styles.backButtonText}>‚Üê Logout</Text>
        </TouchableOpacity>

        <Text style={styles.header}>üî¨ Blood Lab Dashboard</Text>

        <Text style={styles.subheader}>Lab Inventory</Text>
        <FlatList
          scrollEnabled={false}
          data={[
            { id: 1, bloodType: 'O+', component: 'Whole Blood', units: 30 },
            { id: 2, bloodType: 'AB-', component: 'Plasma', units: 12 },
          ]}
          keyExtractor={item => item.id.toString()}
          renderItem={({ item }) => (
            <View style={styles.card}>
              <Text style={styles.cardTitle}>{item.bloodType} ‚Ä¢ {item.component}</Text>
              <Text style={styles.cardText}>{item.units} units</Text>
            </View>
          )}
        />

        <Text style={styles.subheader}>Network Search</Text>
        <TouchableOpacity style={styles.button} onPress={() => Alert.alert('Search', 'Search blood from institutions')}>
          <Text style={styles.buttonText}>üîç Search Network</Text>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

// ============= MAIN APP =============

export default function App() {
  const [appState, setAppState] = useState<'login' | 'profile' | 'roleSelect' | 'donor' | 'hospital' | 'bank' | 'lab'>('login');
  const [user, setUser] = useState<User | null>(null);

  const handleLogin = (credentials: User) => {
    setUser(credentials);
    setAppState('profile');
  };

  const handleProfileComplete = (profile: { fullName: string; bloodType: string }) => {
    setUser(prevUser => prevUser ? { ...prevUser, ...profile } : profile);
    setAppState('roleSelect');
  };

  const handleSelectRole = (role: 'donor' | 'hospital' | 'bank' | 'lab') => {
    setAppState(role);
  };

  const handleLogout = () => {
    setUser(null);
    setAppState('login');
  };

  return (
    <View style={styles.appContainer}>
      {appState === 'login' && <LoginScreen onLogin={handleLogin} />}
      {appState === 'profile' && <ProfileScreen onProfileComplete={handleProfileComplete} />}
      {appState === 'roleSelect' && <RoleSelectScreen onSelectRole={handleSelectRole} />}
      {appState === 'donor' && <DonorScreen onLogout={handleLogout} />}
      {appState === 'hospital' && <HospitalScreen onLogout={handleLogout} />}
      {appState === 'bank' && <BloodBankScreen onLogout={handleLogout} />}
      {appState === 'lab' && <BloodLabScreen onLogout={handleLogout} />}
    </View>
  );
}
